import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from "recharts";
import { Calendar } from "react-big-calendar";
import "react-big-calendar/lib/css/react-big-calendar.css";
import { parseISO } from "date-fns";

export default function Dashboard() {
  const [data, setData] = useState([]);
  // const [monthlySpend, setMonthlySpend] = useState([]);
  const [calendarEvents, setCalendarEvents] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is logged in
    if (!localStorage.getItem("authToken")) {
      navigate("/");
      return;
    }

    console.log(localStorage.getItem("authToken"))

    // Fetch API data
    fetch("http://localhost:8007/api/subscriptions/",{
        method: "GET",
        headers: {
            "Authorization": `Basic ${localStorage.getItem("authToken")}`,
            "Content-Type": "application/json",
        },
        }

    ) // Replace with your real API
      .then((res) => {
      if (!res.ok) {
        throw new Error(`HTTP error! Status: ${res.status}`);
      }
      return res.json();
    })
      .then((result) => {

          console.log("API Data:", result); // Debugging: Check API response
        setData(result.results);
        // setMonthlySpend(result.monthly_spend);
        setCalendarEvents(
          result.results.map((record) => ({
            title: record.plan.service,
              recursion: record.plan.recursion_type_display,
              cost: record.plan.cost,
              currency: record.plan.currency,
            start: parseISO(record.renewal_date),
            end: parseISO(record.renewal_date),
          }))
        );
      })
      .catch((error) => console.error("Error fetching data:", error));
  }, [navigate]);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Dashboard</h1>

      {/* Records Table */}
      <div className="bg-white p-4 shadow rounded-lg mb-6">
        <h2 className="text-xl font-bold mb-4">Records</h2>
        <table className="w-full border-collapse">
          <thead>
          <tr className="bg-gray-200">
              <th className="p-2 border">User</th>
              <th className="p-2 border">Service</th>
              <th className="p-2 border">Plan Type</th>
              <th className="p-2 border">Renewal Date</th>
          </tr>
          </thead>
            <tbody>
            {data.map((record) => (
                <tr key={record.id} className="border">
                    <td className="p-2 border">{record.user}</td>
                    <td className="p-2 border">{record.plan.service}</td>
                    <td className="p-2 border">{record.plan.recursion_type_display}</td>
                    <td className="p-2 border">{record.renewal_date}</td>
                </tr>
            ))}
            </tbody>
        </table>
      </div>

      {/* Monthly Spend Chart */}
      <div className="bg-white p-4 shadow rounded-lg mb-6">
        <h2 className="text-xl font-bold mb-4">Monthly Spend</h2>
        {/*<ResponsiveContainer width="100%" height={300}>*/}
        {/*  <BarChart data={monthlySpend}>*/}
        {/*    <XAxis dataKey="month" />*/}
        {/*    <YAxis />*/}
        {/*    <Tooltip />*/}
        {/*    <Bar dataKey="spend" fill="#4F46E5" />*/}
        {/*  </BarChart>*/}
        {/*</ResponsiveContainer>*/}
      </div>

      {/* Calendar */}
      <div className="bg-white p-4 shadow rounded-lg">
        <h2 className="text-xl font-bold mb-4">Subscription Renewals</h2>
        <Calendar
          localizer={require("react-big-calendar").momentLocalizer(require("moment"))}
          events={calendarEvents}
          startAccessor="start"
          endAccessor="end"
          style={{ height: 400 }}
        />
      </div>
    </div>
  );
}
